import React, { useState } from "react";

export default function Mainpage() {
  // State to manage dark mode
  const [darkMode, setDarkMode] = useState(false);

  // Function to toggle dark mode
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <>
      <div
        className={`w-screen h-screen ${
          darkMode ? "bg-black text-white" : "bg-white text-black"
        }`}
      >
        {/* Button positioned at the top-right corner */}
        <button
          onClick={toggleDarkMode}
          className="absolute top-4 right-4 px-6 py-3 text-xl rounded-lg bg-gray-800 text-white hover:bg-gray-600 dark:bg-gray-300 dark:text-black"
        >
          {darkMode ? "Switch to Light Mode" : "Switch to Dark Mode"}
        </button>

        {/* Two rows with two large buttons each */}
        <div className="flex flex-col justify-center items-center h-full space-y-8">
          {/* Row 1 */}
          <div className="flex space-x-8">
            <div className="w-0.5">
            <button className="px-8 py-4 text-2xl bg-blue-500 text-white rounded-lg hover:bg-blue-700">
              Option 1
            </button>
            </div>
            <button className="px-8 py-4 text-2xl bg-green-500 text-white rounded-lg hover:bg-green-700">
              Option 2
            </button>
          </div>

          {/* Row 2 */}
          <div className="flex space-x-8">
            <button className="px-8 py-4 text-2xl bg-red-500 text-white rounded-lg hover:bg-red-700">
              Option 3
            </button>
            <button className="px-8 py-4 text-2xl bg-yellow-500 text-white rounded-lg hover:bg-yellow-700">
              Option 4
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
