import React, { useState } from "react";
import '../CssFiles/Questions.css';

export default function Question() {
  const Questions = [
    { label: "Name", type: "text" },
    { label: "Age", type: "number" },
    { label: "Occupation", type: "text" },
    { label: "Race", type: "dropdown", options: ["Asian", "Black", "White", "Hispanic", "Other"] },
    { label: "Relationship Status", type: "radio", options: ["Single", "Married", "Divorced"] },
    { label: "Gender", type: "radio", options: ["Male", "Female", "Other"] }
  ];

  const [currentInd, setCurrentInd] = useState(0);

  const renderInput = (question) => {
    switch (question.type) {
      case "text":
      case "number":
        return <input type={question.type} className="input-field" placeholder={`Enter your ${question.label}`} />;
      case "dropdown":
        return (
          <select className="input-field">
            {question.options.map((option, index) => (
              <option key={index} value={option}>{option}</option>
            ))}
          </select>
        );
      case "radio":
        return question.options.map((option, index) => (
          <label key={index} className="radio-option">
            <input type="radio" name={question.label} value={option} /> {option}
          </label>
        ));
      default:
        return null;
    }
  };

  const handleNext = () => {
    setCurrentInd((prevIndex) => (prevIndex + 1) % Questions.length);
  };

  const handlePrev = () => {
    setCurrentInd((prevIndex) => (prevIndex - 1 + Questions.length) % Questions.length);
  };

  return (
    <>
      <div className="projects-section bg-blue-500">
        <h1 className="text-7xl title">Questions</h1>
        <div className="scroll-container">
          <button onClick={handlePrev} className="carousel-button left-button">❮</button>
          
          <div className="project-card m-auto">
            <h1>{Questions[currentInd].label}</h1>
            {renderInput(Questions[currentInd])}
          </div>
          
          <button onClick={handleNext} className="carousel-button right-button">❯</button>
        </div>
      </div>
    </>
  );
}
